import { useParams } from "react-router-dom";
import { MakerNav } from "../../../component/Navbar/homeNav";
import {useEffect,useState} from 'react';
import './PrinterDetails.css';
import OnBoarding from "../../../SmartContracts/OnBoarding/OnBoarding";
import InfoComponent from "../../../component/PrinterInfoComponent/Info/InfoComponent";
import UpdateComponent from "../../../component/PrinterInfoComponent/Update/UpdateComponent";
import RemoveComponent from "../../../component/PrinterInfoComponent/Remove/RemoveComponent";

export default function PrinterDetails(){

    let param = useParams();
    //console.log(param)
    let reg=/&&([0-9])/g;
    const index=reg.exec(param.printerDetails);
    //const [myPrinter,setMyPrinter]=useState();
    const title=['Printer Info','Update Material','Remove Material'];
    const [currentTitle,setCurrentTitle]=useState(title[0]);
    const target={        
        info:false,
        update:false,
        remove:false
    }
    const [section,setSection]=useState({        
        info:true,
        update:false,
        remove:false
    });

    //let printer=getPrinter(param.printerDetails);

    function setNewSection(currentSection){
        let result='';
        switch(currentSection){
            case 'info': result=Object.assign(target,{info:true});
                setSection(result);
                setCurrentTitle(title[0]); 
                break;
            case 'Update': result=Object.assign(target,{update:true});
                setSection(result);
                setCurrentTitle(title[1]);
                break;
            case 'Remove': result=Object.assign(target,{remove:true});
                setSection(result);
                setCurrentTitle(title[2]);
                break;
            default: break;
        }


    }


    useEffect(()=>{


       /* const  getPrinterInfo=async()=>{
            const onboard=new OnBoarding();
            const result=await onboard.getPrinter(index[1]);
            setMyPrinter(result);

        };
        getPrinterInfo();*/
    })
    return (
        <div>
        <MakerNav/>
        <div className="title">
            <h2>{currentTitle}</h2>
        </div>
        <div className ="container">
            <div className="navBarPrinter">
                    <p className="sectionStyle" style={{textDecoration:section.info? 'underline': '',
                        color: section.info? 'rgb(130, 195, 255)': ''}} onClick={()=>{
                        if(section.info!==true)
                            setNewSection('info');
                    }}>Info</p>
                    <p className="sectionStyle" style={{textDecoration:section.update? 'underline': '',
                        color: section.update? 'rgb(130, 195, 255)': ''}} onClick={()=>{
                        if(section.update!==true)
                            setNewSection('Update');
                    }}>Update</p>
                    <p className="sectionStyle" style={{textDecoration:section.remove? 'underline': '',
                        color: section.remove? 'rgb(130, 195, 255)': ''}} onClick={()=>{
                        if(section.remove!==true)
                            setNewSection('Remove');
                    }}>Remove</p>
            </div>


        </div>

        </div>
    )   
}
