import React from 'react'
import { UserNav } from '../../component/Navbar/homeNav'
import { useEffect } from 'react';
import OnBoarding from '../../SmartContracts/OnBoarding/OnBoarding';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Web3 from "web3"
import { getConstMaterials,getColor } from "../../data/data";

export default function MyMaterials() {

  const [materialsList,SetMaterialsList]=useState([])
  const navigate = useNavigate();
  const [action,SetAction] = useState("");

  const material=[
    {value:"0" , label:"ABS"},
    {value:"1",label:"PLA"},
    {value:"2",label:"PETG"}]  

  const colors=[
    {value:"0", label:"NONE"},
    {value:"1", label:"BLACK"},
    {value:"2", label:"WHITE"},
    {value:"3", label:"BROWN"},
    {value:"4",label:"GRAY"},
    {value:"5", label:"YELLOW"},
    {value:"6", label:"ORANGE"},
    {value:"7", label:"RED"},
    {value:"8", label:"PINK"},
    {value:"9", label:"PURPLE"},
    {value:"10", label:"BLU"},
    {value:"11", label:"GREEN"}]


  let result="prova"

  const removeMaterial=(async(name,type,index)=>{
    console.log(name,type,index)
    const onboard=new OnBoarding();
    let res=await onboard.removeMaterial(name,type,index);
    if(res!=='Error')
        window.location.reload();
})

  const handleSubmit = async(name,type,index)=>{
    const onboarding = new OnBoarding();
    await onboarding.removeMaterial(name, type, index)
    window.location.reload()
  }

  useEffect(()=>{
    async function getMaterials(){
      const web3=new Web3()
      const boarding=new OnBoarding();
      let res=await boarding.getMaterials();
      console.log(res);
      if(res==='Error')
          return
      const material=getConstMaterials();
      const color=getColor()
      result=res.map((value,key)=>{
          return <tr key={key} className="printers" >
              <td key={value.name+"name"} className="color" onClick={()=>navigate('/')}> {web3.utils.toUtf8(value.name)} </td>
              <td key={value.color+"color"}  style={{width:"10px"}} >{color[value.color]}</td>
              <td key={value.mType}  style={{width:"10px"}} >  {material[value.mType]} </td>
              <td key={key+"button"} style={{width:"10px"}}><button className="remove" onClick={()=>removeMaterial(value.name,value.mType,key)}>Rimuovi</button></td>
          </tr>  


      })
      console.log(result)
      SetMaterialsList(result)
    }
    getMaterials();
  },[])

  return (
    <div>
        <div className='sign'>
        <UserNav/>
        <h1>My Materials</h1>

        <table className='myPrinters'>

          <tbody>
            <tr>
              <th>name</th>
              <th>Type</th>
              <th>Color</th>
              <th>quantityKG</th>
              <th>Print Temp</th>
              <th>Bed Temp</th>
              <th>Remove</th>
              <th>Update</th>
            </tr>
            {materialsList}
          </tbody>
        </table>
        <div className='SignIn'>
        <button className='next1' onClick={()=>navigate('/AddMaterials')}>Add Materials</button>
        </div>
        </div>

    </div>
  )
}
