import Select from 'react-select';
import { MakerNav } from '../../component/Navbar/homeNav'; 
import { useState } from 'react';
import { handleChange } from '../../Functions/handleChange';

export default function AddMaterial(){
    let  materialData={};
    const [val,setVal]=useState({});
    const material=[
        {value:"0" , label:"ABS"},
        {value:"1",label:"PLA"},
        {value:"2",label:"PETG"}];
    const color=[
        {value:"1" , label:"BLACK"},
        {value:"2",label:"WHITE"},
        {value:"3",label:"BROWN"},
        {value:"4",label:"GRAY"},
        {value:"5",label:"YELLOW"},
        {value:"6",label:"ORANGE"},
        {value:"7",label:"RED"},
        {value:"8",label:"PINK"},
        {value:"9",label:"PURPLE"},
        {value:"10",label:"BLUE"},
        {value:"11",label:"GREEN"}];

    const [section,setSection]=useState(1);

    const handleSubmit=(()=>{

    })




    return(
        <div className='sign'>
            <MakerNav/>
            <h1>Add Material</h1>
            <div className='SignIn'>
            <form  onSubmit={handleSubmit}>
            <div className="sectionPage" style={{display:section===1?('block'):('none')}}>

                    <label>
                            <p>Material Name</p>
                            <input type="text" style={{display:'inline'}} name="address" onChange={(event) =>{ setVal((val)=>({...val,'materialName':event.target.value}))}} autoComplete="off" required minLength="4" maxLength="90"></input>
                    </label>
                    <label>
                        <p>Material Print Temperature</p>
                        <input type="number" id="tentacles" name="tentacles"
                            min="40" max="90" onChange={event => {setVal((val)=>({...val,'materialTemp':event.target.value}))}}/>
                    </label>


                <label>
                    <p>Material Bed Temperature</p>
                    <input type="number" id="tentacles" name="tentacles"
                        min="40" max="90" onChange={event => {setVal((val)=>({...val,'materialBedTemp':event.target.value}))}}/>
                </label>
            </div>
            <div className="sectionPage" style={{display:section===2?('block'):('none')}}>

                <label>
                    <p>Quantity (Kg)</p>
                    <input type="number" id="tentacles" name="tentacles"
                        min="1" max="10" onChange={event => {setVal((val)=>({...val,'materialQuantity':event.target.value}))}}/>
                </label>

                <label>
                    <p>Material Color</p>
                        <div style={{
                            width:"200px",
                            margin:"auto"
                            }}>
                    <Select  isMulti 
                        options={color}
                        getOptionValue={(option)=>option.value}
                        onChange={(option)=>{
                            let result=handleChange(option,color);
                            console.log("result");
                            setVal((val)=>({...val,'color':result}))
                            }}/>
                        </div>
                    </label>

                <label>
                    <p>Material</p>
                        <div style={{
                            width:"200px",
                            margin:"auto"
                            }}>
                    <Select  isMulti 
                        options={material}
                        getOptionValue={(option)=>option.value}
                        onChange={(option)=>{
                            let result=handleChange(option,'material')
                            console.log(result);
                            setVal((val)=>({...val,'material':result}))
                            }}/>
                        </div>
                    </label>


                </div>
                <div className='elementInsertion'>
                {                   

                                    section>1
                                        &&

                <button className="next1" type='button' onClick={()=>setSection((prev)=>prev-1)}><p>&#60;</p></button>

                }
                {                       section!==2
                                            &&
                     <button className="next1" type='button' 
                        onClick={()=>setSection((prev)=>prev+1)}><p>&#62;</p></button>
                }

                {section===2 && <button className="next2" style={{padding:"10px"}}type='submit'>Aggiungi</button>}
                </div>
            </form>
        </div>
    </div>
    )

}
